import React, { Component } from "react";
import { connect } from "react-redux";
import { getGenres, addVideogame } from "../../actions";

var tmpPlatforms = ["PC", "PS5", "PS4", "XBOXONE"];

export class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      description: "",
      releaseDate: "",
      rating: "",
      genres: [],
      platforms: [],
      petition: {
        loading: false,
        error: null,
      },
      isSubmitted: false,
    };
  }

  componentDidMount() {
    this.setState({
      petition: {
        loading: true,
        error: null,
      },
    });
    this.props.getGenres().then(() => {
      this.setState({
        petition: {
          loading: false,
          error: null,
        },
        isSubmitted: true,
      });
    });
  }

  handleSubmit(event) {
    event.preventDefault();
    console.log("SUBMIT");
    this.props.uploadVideogame({
      name: this.state.name,
      description: this.state.description,
      releaseDate: this.state.releaseDate,
      rating: this.state.rating,
      genres: this.state.genres,
      platform: this.state.platforms,
    });
  }

  handleKeyPress = (e) => {
    if (e.keyCode === 13) {
      this.handleSubmit();
    }
  };

  genresList() {
    if (!this.state.petition.loading) {
      return this.props.genres.response.map((element, i) => {
        return (
          <option key={i} value={element.name}>
            {element.name}
          </option>
        );
      });
    }
  }

  handleChange(e) {
    this.setState({ [e.target.id]: e.target.value });
  }

  platformsList() {
    if (!this.state.petition.loading) {
      return tmpPlatforms.map((element, i) => {
        return (
          <option key={i} value={element}>
            {element}
          </option>
        );
      });
    }
  }

  addGenres(e) {
    var newArray = this.state.genres;
    var tmp = true;
    for (var i = 0; i < newArray.length; i++) {
      if (newArray[i] === e.target.value) {
        newArray.splice(i, 1);
        tmp = false;
      }
    }
    if (tmp === true) {
      this.setState({
        genres: [...this.state.genres, e.target.value],
      });
      return;
    } else {
      this.setState({
        genres: newArray,
      });
    }
  }

  addPlatform(e) {
    var newArray = this.state.platforms;
    var tmp = true;
    for (var i = 0; i < newArray.length; i++) {
      if (newArray[i] === e.target.value) {
        newArray.splice(i, 1);
        tmp = false;
      }
    }
    if (tmp === true) {
      this.setState({
        platforms: [...this.state.platforms, e.target.value],
      });
      return;
    } else {
      this.setState({
        platforms: newArray,
      });
    }
  }

  render() {
    const {
      name,
      description,
      releaseDate,
      rating,
      genres,
      platforms,
    } = this.state;
    return (
      <div>
        <form onSubmit={(e) => this.handleSubmit(e)}>
          <div>
            <label htmlFor="name">name: </label>
            <input
              type="text"
              id="name"
              autoComplete="off"
              value={name}
              onChange={(e) => this.handleChange(e)}
            />
          </div>
          <div>
            <label htmlFor="description">description: </label>
            <input
              type="text"
              id="description"
              autoComplete="off"
              value={description}
              onChange={(e) => this.handleChange(e)}
            />
          </div>
          <div>
            <label htmlFor="releaseDate">release date: </label>
            <input
              type="text"
              id="releaseDate"
              autoComplete="off"
              value={releaseDate}
              onChange={(e) => this.handleChange(e)}
            />
          </div>
          <div>
            <label htmlFor="rating">rating: </label>
            <input
              type="number"
              id="rating"
              autoComplete="off"
              value={rating}
              onChange={(e) => this.handleChange(e)}
            />
          </div>
          <div>
            <label htmlFor="genres">genres: </label>
            <select
              name="genres"
              id="genres"
              multiple={true}
              value={genres}
              onChange={(e) => this.addGenres(e)}
            >
              {this.state.isSubmitted && this.genresList()}
            </select>
          </div>
          <div>
            <label htmlFor="platforms">platforms: </label>
            <select
              name="platforms"
              id="platforms"
              multiple={true}
              value={platforms}
              onChange={(e) => this.addPlatform(e)}
            >
              {this.state.isSubmitted && this.platformsList()}
            </select>
          </div>
          <button type="submit">SEARCH</button>
        </form>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    genres: state.genres,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    getGenres: () => dispatch(getGenres()),
    uploadVideogame: (payload) => dispatch(addVideogame(payload)),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Home);
