import {
  ADD_VIDEOGAME,
  GET_GENRES,
  SEARCH_VIDEOGAME,
  VIDEOGAME_DETAIL,
} from "../actiontypes";
const initialState = {
  videogames: [],
  searchMsg: "",
  videogamesLoaded: [],
  videogameDetail: {},
  allGenres: [],
};

export default function rootReducer(state = initialState, action) {
  switch (action.type) {
    case SEARCH_VIDEOGAME:
      return {
        ...state,
        videogames: action.payload.data.response,
        searchMsg: action.payload.data.msg,
      };
    case GET_GENRES:
      return {
        ...state,
        genres: action.payload.data,
      };
    default:
      return state;
  }
}
