import React, { Component } from "react";
import { connect } from "react-redux";
import { searchVideogame } from "../../actions";

const GamesMap = (props) => {
  var stringGenres = "";

  props.videogame.genres.forEach((element) => {
    stringGenres += element.name + " ";
  });

  return (
    <tr>
      <td>{props.videogame.tmpid + 1}</td>
      <td>{props.videogame.name}</td>
      <td>{stringGenres}</td>
      <td>
        <img
          src={props.videogame.image}
          alt={`${props.videogame.name} background`}
          width="20px"
          height="20px"
        />
      </td>
    </tr>
  );
};

export class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "",
      ordering: "up",
      isSubmitted: false,
      petition: {
        loading: false,
        msg: null,
      },
    };
  }

  handleChange(event) {
    this.setState({ title: event.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();
    this.setState({
      petition: {
        loading: true,
        error: null,
      },
    });
    this.props.getVideogame(this.state.title).then(() => {
      this.setState({
        petition: {
          loading: false,
          error: null,
        },
        isSubmitted: true,
      });
    });
  }

  handleKeyPress = (e) => {
    if (e.keyCode === 13) {
      this.handleSubmit();
    }
  };

  videogamesMap() {
    if (
      this.props.videogames.hasOwnProperty("results") &&
      !this.state.petition.loading
    ) {
      switch (this.state.ordering) {
      // CASE NAME DOWN
        case "name-down":
          var tmp1 = this.props.videogames.results;
          tmp1.sort((a, b) => {
            let fa = a.name.toLowerCase(),
              fb = b.name.toLowerCase();
            if (fa < fb) {
              return -1;
            }
            if (fa > fb) {
              return 1;
            }
            return 0;
          });
          tmp1.reverse();
          return tmp1.map((game) => {
            return <GamesMap videogame={game} key={game.tmpid} />;
          });
        // CASE NAME UP
        case "name-up":
          var tmp = this.props.videogames.results;
          tmp.sort((a, b) => {
            let fa = a.name.toLowerCase(),
              fb = b.name.toLowerCase();
            if (fa < fb) {
              return -1;
            }
            if (fa > fb) {
              return 1;
            }
            return 0;
          });
          return tmp.map((game) => {
            return <GamesMap videogame={game} key={game.tmpid} />;
          });
        // CASE DEFAULT
        default:
          return this.props.videogames.results.map((game) => {
            return <GamesMap videogame={game} key={game.id} />;
          });
      }
    }
  }

  render() {
    const { title } = this.state;
    return (
      <div>
        <p>Search input</p>
        <form onSubmit={(e) => this.handleSubmit(e)}>
          <div>
            <label htmlFor="title">Videogame: </label>
            <input
              type="text"
              id="title"
              autoComplete="off"
              value={title}
              onChange={(e) => this.handleChange(e)}
              onKeyPress={(e) => this.handleKeyPress(e)}
            />
          </div>
          <button type="submit">SEARCH</button>
        </form>
        {this.state.isSubmitted && (
          <>
            {this.props.searchMsg}
            <table>
              <thead>
                <tr>
                  <td>
                    <a href="dasd">id</a>
                  </td>
                  <td>
                    <a href="sf">name</a>
                  </td>
                  <td>
                    <a href="sf">genres</a>
                  </td>
                  <td>
                    <a href="sad">image</a>
                  </td>
                </tr>
              </thead>
              <tbody>
                {this.videogamesMap()}
                <tr>
                  <td>
                    <a href="egs">previous</a>
                  </td>
                  <td></td>
                  <td></td>
                  <td>
                    <a href="fs">next</a>
                  </td>
                </tr>
              </tbody>
            </table>
          </>
        )}
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    videogames: state.videogames,
    searchMsg: state.searchMsg,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    getVideogame: (name) => dispatch(searchVideogame(name)),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Home);
