import React, { Component } from "react";
import { connect } from "react-redux";
import { NavLink } from "react-router-dom";
import { searchVideogame } from "../../actions";
import "./home.css";

const GamesMap = (props) => {
  var stringGenres = "";

  props.videogame.genres.forEach((element) => {
    stringGenres += element.name + " ";
  });

  return (
    <div className="page__list__card">
      <div className="page__list__card-img">
        <img
          className="image"
          src={props.videogame.image}
          alt={`${props.videogame.name} background`}
        >
          {props.videogame.image}
        </img>
      </div>
      <div className="page__list__card-title">
        <NavLink className="title" to={`/videogame/${props.videogame.id}`}>{props.videogame.name}</NavLink> 
      </div>
    </div>
    // <tr>
    //   <td>{props.videogame.tmpid + 1}</td>
    //   <td>
    //     <NavLink exact to={`/videogame/${props.videogame.id}`}>
    //       {props.videogame.name}
    //     </NavLink>
    //   </td>
    //   <td>{stringGenres}</td>
    //   <td>
    //     <img
    //       src={props.videogame.image}
    //       alt={`${props.videogame.name} background`}
    //       width="20px"
    //       height="20px"
    //     />
    //   </td>
    // </tr>
  );
};

export class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "",
      ordering: "up",
      isSubmitted: false,
      petition: {
        loading: false,
        msg: null,
      },
    };
  }

  handleChange(event) {
    this.setState({ title: event.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();
    this.setState({
      petition: {
        loading: true,
        error: null,
      },
    });
    this.props.getVideogame(this.state.title).then(() => {
      this.setState({
        petition: {
          loading: false,
          error: null,
        },
        isSubmitted: true,
      });
    });
  }

  handleKeyPress = (e) => {
    if (e.keyCode === 13) {
      this.handleSubmit();
    }
  };

  pagination(e) {}

  // EXTENSIVE CODE AHEAD FOR THIS FUNCTION !!NEEDS RECODE!!
  videogamesMap() {
    if (
      this.props.videogames.hasOwnProperty("results") &&
      !this.state.petition.loading
    ) {
      switch (this.state.ordering) {
        // CASE NAME DOWN
        case "name-down":
          var tmp1 = this.props.videogames.results;
          tmp1.sort((a, b) => {
            let fa = a.name.toLowerCase(),
              fb = b.name.toLowerCase();
            if (fa < fb) {
              return -1;
            }
            if (fa > fb) {
              return 1;
            }
            return 0;
          });
          tmp1.reverse();
          return tmp1.map((game) => {
            return <GamesMap videogame={game} key={game.tmpid} />;
          });
        // CASE NAME UP
        case "name-up":
          var tmp = this.props.videogames.results;
          tmp.sort((a, b) => {
            let fa = a.name.toLowerCase(),
              fb = b.name.toLowerCase();
            if (fa < fb) {
              return -1;
            }
            if (fa > fb) {
              return 1;
            }
            return 0;
          });
          return tmp.map((game) => {
            return <GamesMap videogame={game} key={game.tmpid} />;
          });
        // CASE DEFAULT
        case "id-up":
          var tmpidUp = this.props.videogames.results;
          tmpidUp.sort((a, b) => {
            return a.tmpid - b.tmpid;
          });
          return tmpidUp.map((game) => {
            return <GamesMap videogame={game} key={game.tmpid} />;
          });
        case "id-down":
          var tmpidDown = this.props.videogames.results;
          tmpidDown.sort((a, b) => {
            return a.tmpid - b.tmpid;
          });
          tmpidDown.reverse();
          return tmpidDown.map((game) => {
            return <GamesMap videogame={game} key={game.tmpid} />;
          });
        default:
          return this.props.videogames.results.map((game) => {
            return <GamesMap videogame={game} key={game.tmpid} />;
          });
      }
    }
  }
  // ----------!!END OF EXTENSIVE FUNCTION!!---------- //

  orderingFunction(event) {
    const { id } = event.target;
    switch (id) {
      case "name":
        if (event.target.value === "name-up") {
          event.target.value = "name-down";
          this.setState({
            ordering: "name-down",
          });
        } else {
          event.target.value = "name-up";
          this.setState({
            ordering: "name-up",
          });
        }
        break;
      case "id":
        if (event.target.value === "id-up") {
          event.target.value = "id-down";
          this.setState({
            ordering: "id-down",
          });
        } else {
          event.target.value = "id-up";
          this.setState({
            ordering: "id-up",
          });
        }
        break;
      default:
    }
  }

  render() {
    const { title } = this.state;
    return (
      <div id="page__leader" className="fullsize">
        <div className="container-home">
          <div className="page__head">
            <div className="page__head-meta">
              <h1 className="heading title">Search any videogame</h1>
            </div>
          </div>
          <div className="page__form">
            <form
              className="page__search__form"
              onSubmit={(e) => this.handleSubmit(e)}
              role="search"
            >
              <div className="page__search__input__area">
                <input
                  type="text"
                  id="title"
                  className="page__form-input"
                  placeholder="Tomb Raider..."
                  autoFocus={true}
                  autoComplete="off"
                  value={title}
                  onChange={(e) => this.handleChange(e)}
                  onKeyPress={(e) => this.handleKeyPress(e)}
                />
              </div>
              <button className="page__form-submit" type="submit">
                search
              </button>
            </form>
          </div>
          <div className="page__list__games">
            {this.state.isSubmitted && (
              <div className="page_list_cards">
                {this.props.searchMsg}
                {this.videogamesMap()}
                {/* <table className="page__list-table">
                  <thead>
                    <tr>
                      <td>
                        <p
                          id="id"
                          value="id-up"
                          onClick={(e) => this.orderingFunction(e)}
                        >
                          id
                        </p>
                      </td>
                      <td>
                        <p
                          id="name"
                          value="name-up"
                          onClick={(e) => this.orderingFunction(e)}
                        >
                          name
                        </p>
                      </td>
                      <td>
                        <a href="sf">genres</a>
                      </td>
                      <td>
                        <a href="sad">image</a>
                      </td>
                    </tr>
                  </thead>
                  <tbody>
                    {this.videogamesMap()}
                    <tr>
                      <td>
                        <a href="egs">previous</a>
                      </td>
                      <td></td>
                      <td></td>
                      <td>
                        <p value={this.props.videogames.next}>next</p>
                      </td>
                    </tr>
                  </tbody>
                </table> */}
              </div>
            )}
          </div>
        </div>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    videogames: state.videogames,
    searchMsg: state.searchMsg,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    getVideogame: (name) => dispatch(searchVideogame(name)),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Home);
