import React, { Component } from "react";
import { connect } from "react-redux";
import { searchVideogame } from "../../actions";

const GamesMap = (props) => (
  <tr>
    <td>{props.videogame.tmpid + 1}</td>
    <td>{props.videogame.name}</td>
    <td>
      <img
        src={props.videogame.image}
        alt={`${props.videogame.name} background`}
        width="20px"
        height="20px"
      />
    </td>
  </tr>
);

export class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "",
      ordering: "up",
    };
  }
  handleChange(event) {
    this.setState({ title: event.target.value });
  }
  handleSubmit(event) {
    event.preventDefault();
    this.props.getVideogame(this.state.title);
  }
  handleKeyPress = (e) => {
    if (e.keyCode === 13) {
      this.handleSubmit();
    }
  };
  videogamesMap() {
    switch (this.state.ordering) {
      case "default":
        return this.props.videogames.results.map((game) => {
          return <GamesMap videogame={game} key={game.id} />;
        });
      default:
        return this.props.videogames.results.map((game) => {
          return <GamesMap videogame={game} key={game.id} />;
        });
    }
  }

  render() {
    const { title } = this.state;
    return (
      <div>
        <p>Search input</p>
        <form onSubmit={(e) => this.handleSubmit(e)}>
          <div>
            <label htmlFor="title">Videogame: </label>
            <input
              type="text"
              id="title"
              autoComplete="off"
              value={title}
              onChange={(e) => this.handleChange(e)}
              onKeyPress={(e) => this.handleKeyPress(e)}
            />
          </div>
          <button type="submit">SEARCH</button>
        </form>
        <table>
          <thead>{this.props.searchMsg}</thead>
          <tbody>
            {this.videogamesMap()}
            <tr>
              <td>
                <a href="egs">previous</a>
              </td>
              <td>actual</td>
              <td>
                <a href="fs">next</a>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    videogames: state.videogames,
    searchMsg: state.searchMsg,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    getVideogame: (name) => dispatch(searchVideogame(name)),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Home);
