import React, { Component } from "react";
import { connect } from "react-redux";
import { NavLink } from "react-router-dom";
import { searchVideogame } from "../../actions";
import { Loader } from "../loader";
import { randEmoji } from "../../idk/random-emoji";
import "./home.css";

const GamesMap = (props) => {
  var stringGenres = "";

  props.videogame.genres.forEach((element) => {
    stringGenres += element.name + " ";
  });

  function onMouseEnter(e) {
    // e.target.classList.remove("hidden");
    var tmp = document.querySelector(`#genre_${props.videogame.id}`);
    tmp.classList.add("hidden");
  }

  function onMouseLeave(e) {
    // e.target.classList.add("hidden");
    var tmp = document.querySelector(`#genre_${props.videogame.id}`);
    tmp.classList.remove("hidden");
  }

  return (
    <div
      className="page__list__cards-card"
      onMouseOver={(e) => onMouseEnter(e)}
      onMouseLeave={(e) => onMouseLeave(e)}
    >
      <div className="page__list__card-img">
        <img
          className="card__image"
          src={props.videogame.image}
          alt={`${props.videogame.name} background`}
        />
      </div>
      <div className="page__list__card-title">
        <div>
          <NavLink
            className="card__title card__link"
            to={`/videogame/${props.videogame.id}`}
          >
            {props.videogame.name + randEmoji()}
            {/* &#128076; */}
          </NavLink>
        </div>
        <div
          id={`genre_${props.videogame.id}`}
          className="page__list__card-genres"
        >
          <div>genres:</div>
          <div className="">{stringGenres}</div>
        </div>
      </div>
    </div>
  );
};

export class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "",
      ordering: "up",
      isSubmitted: false,
      petition: {
        loading: false,
        msg: null,
      },
    };
  }

  handleChange(event) {
    this.setState({ title: event.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();
    this.setState({
      petition: {
        loading: true,
        error: null,
      },
      isSubmitted: false,
    });

    this.props
      .getVideogame(this.state.title)
      .then(() => {
        this.setState({
          petition: {
            loading: false,
            error: null,
          },
          isSubmitted: true,
        });
      })
      .catch((err) => console.error("GAME NOT FOUND!!!", err.message));
  }

  handleKeyPress = (e) => {
    if (e.keyCode === 13) {
      this.handleSubmit();
    }
  };

  pagination(e) {}

  // EXTENSIVE CODE AHEAD FOR THIS FUNCTION !!NEEDS RECODE!!
  videogamesMap() {
    if (
      this.props.videogames.hasOwnProperty("results") &&
      !this.state.petition.loading
    ) {
      switch (this.state.ordering) {
        // CASE NAME DOWN
        case "name-down":
          var tmp1 = this.props.videogames.results;
          tmp1.sort((a, b) => {
            let fa = a.name.toLowerCase(),
              fb = b.name.toLowerCase();
            if (fa < fb) {
              return -1;
            }
            if (fa > fb) {
              return 1;
            }
            return 0;
          });
          tmp1.reverse();
          return tmp1.map((game) => {
            return <GamesMap videogame={game} key={game.tmpid} />;
          });
        // CASE NAME UP
        case "name-up":
          var tmp = this.props.videogames.results;
          tmp.sort((a, b) => {
            let fa = a.name.toLowerCase(),
              fb = b.name.toLowerCase();
            if (fa < fb) {
              return -1;
            }
            if (fa > fb) {
              return 1;
            }
            return 0;
          });
          return tmp.map((game) => {
            return <GamesMap videogame={game} key={game.tmpid} />;
          });
        // CASE DEFAULT
        case "id-up":
          var tmpidUp = this.props.videogames.results;
          tmpidUp.sort((a, b) => {
            return a.tmpid - b.tmpid;
          });
          return tmpidUp.map((game) => {
            return <GamesMap videogame={game} key={game.tmpid} />;
          });
        case "id-down":
          var tmpidDown = this.props.videogames.results;
          tmpidDown.sort((a, b) => {
            return a.tmpid - b.tmpid;
          });
          tmpidDown.reverse();
          return tmpidDown.map((game) => {
            return <GamesMap videogame={game} key={game.tmpid} />;
          });
        default:
          return this.props.videogames.results.map((game) => {
            return <GamesMap videogame={game} key={game.tmpid} />;
          });
      }
    }
  }
  // ----------!!END OF EXTENSIVE FUNCTION!!---------- //

  orderingFunction(event) {
    const { id } = event.target;
    switch (id) {
      case "name":
        if (event.target.value === "name-up") {
          event.target.value = "name-down";
          this.setState({
            ordering: "name-down",
          });
        } else {
          event.target.value = "name-up";
          this.setState({
            ordering: "name-up",
          });
        }
        break;
      case "id":
        if (event.target.value === "id-up") {
          event.target.value = "id-down";
          this.setState({
            ordering: "id-down",
          });
        } else {
          event.target.value = "id-up";
          this.setState({
            ordering: "id-up",
          });
        }
        break;
      default:
    }
  }

  render() {
    const { title } = this.state;
    return (
      <div id="page__leader">
        <div className="container-home">
          <div className="page__head">
            <div className="page__head-meta">
              <h1 className="heading title">Search any videogame</h1>
            </div>
          </div>
          <div className="page__form">
            <form
              className="page__search__form"
              onSubmit={(e) => this.handleSubmit(e)}
              role="search"
            >
              <div className="page__search__input__area">
                <input
                  type="text"
                  id="title"
                  className="page__form-input"
                  placeholder="Tomb Raider..."
                  autoFocus={true}
                  autoComplete="off"
                  value={title}
                  onChange={(e) => this.handleChange(e)}
                  onKeyPress={(e) => this.handleKeyPress(e)}
                />
              </div>
              <button className="page__form-submit" type="submit">
                search
              </button>
            </form>
          </div>
          <div className="page__list__games">
            {this.state.petition.loading && (
              <div>
                <Loader />
              </div>
            )}
            {this.state.isSubmitted && (
              <>
                <div className="page__divbar">
                  <div className="page__divbar-content">
                    <h1>Order By</h1>
                    <div className="page__divbar-content">
                      <p>Name</p>
                      <p>id</p>
                    </div>
                  </div>
                </div>
                <div className="page__list__cards">
                  {/* {this.props.searchMsg} */}
                  {this.videogamesMap()}
                </div>
              </>
            )}
          </div>
        </div>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    videogames: state.videogames,
    searchMsg: state.searchMsg,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    getVideogame: (name) => dispatch(searchVideogame(name)),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Home);
