import React, { Component } from "react";
import { connect } from "react-redux";
import { searchVideogame } from "../../actions";
import "./detail.css";

const Maps = (props) => (
  <>
    <li>{props.game.name}</li>
  </>
);

export class DetailGame extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoaded: false,
    };
  }

  componentDidMount() {
    const { name } = this.props.match.params;
    this.props.getVideogame({ id: name }).then(() => {
      this.setState({ isLoaded: true });
      console.log("GAME LOADED");
    });
  }

  list(opt) {
    if (opt === "genres") {
      return this.props.detail.genres.map((game) => {
        return <Maps game={game} key={game.id} />;
      });
    } else {
      return this.props.detail.platforms.map((platform) => {
        return <Maps game={platform.platform} key={platform.platform.id} />;
      });
    }
  }

  render() {
    return (
      <div className="container">
        {this.state.isLoaded && (
          <>
            <div className="page__head">
              <div className="page_head-meta">
                <p>{this.props.detail.rating}</p>
                <p>{this.props.detail.releaseDate}</p>
              </div>
              <h1>{this.props.detail.name}</h1>
            </div>
            <img src={this.props.detail.image} alt="ni idea" />

            <div className="smallDetails">
              <div></div>
              <div></div>
            </div>
            <h3>{this.props.detail.description}</h3>
            <div>
              <p>Genres List:</p>
              <ol>{this.list("genres")}</ol>
            </div>
            <div>
              <p>Platforms List:</p>
              <ol>{this.list()}</ol>
            </div>
          </>
        )}
      </div>
    );
  }
}

function mapStateToProps(state) {
  const {
    id,
    genres,
    background_image,
    description_raw,
    released,
    rating,
    name,
    platforms,
  } = state.videogames;
  return {
    detail: {
      id,
      genres,
      image: background_image,
      description: description_raw,
      releaseDate: released,
      rating,
      name,
      platforms,
    },
  };
}

function mapDispatchToProps(dispatch) {
  return {
    getVideogame: (name) => dispatch(searchVideogame(name)),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(DetailGame);
