import React, { Component } from "react";
import { connect } from "react-redux";
import { searchVideogame } from "../../actions";

const GamesMap = (props) => (
  <tr>
    <td>hola</td>
  </tr>
);

export class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "",
    };
  }
  handleChange(event) {
    this.setState({ title: event.target.value });
  }
  handleSubmit(event) {
    event.preventDefault();
    const tmp = this.props.getVideogame(this.state.title);
    console.log(tmp);
  }
  handleKeyPress = (e) => {
    if (e.keyCode === 13) {
      this.handleSubmit();
    }
  };
  videogamesMap() {
    return this.props.videogames.map((game) => {
      return <GamesMap videogame={game} />;
    });
  }

  render() {
    const { title } = this.state;
    return (
      <div>
        <p>Search input</p>
        <form onSubmit={(e) => this.handleSubmit(e)}>
          <div>
            <label htmlFor="title">Videogame: </label>
            <input
              type="text"
              id="title"
              autoComplete="off"
              value={title}
              onChange={(e) => this.handleChange(e)}
              onKeyPress={(e) => this.handleKeyPress(e)}
            />
          </div>
          <button type="submit">SEARCH</button>
        </form>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    videogames: state.videogamesLoaded,
  };
};

const mapDispatchToProps = () => {
  return {
    getVideogame: (name) => dispatch(searchVideogame(name)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Home);
