import React, { Component } from "react";
import { connect } from "react-redux";
import { searchVideogame } from "../../actions";

export class DetailGame extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoaded: false,
    };
  }

  componentDidMount() {
    const { id } = this.props.match.params;
    this.props.getVideogame({ id }).then(() => console.log("LOADED GAME"));
  }

  render() {
    const {
      name,
      description,
      rating,
      image,
      genres,
      releaseDate,
      platforms,
    } = this.props.videogames;
    return (
      <div>
        {this.state.isLoaded && (
          <>
            <h1>Name: {name}</h1>
            <h3>Description: {description}</h3>
            <p>Rating: {rating}</p>
            <p>Release date: {releaseDate}</p>
            <img src={image} alt="ni idea" />
          </>
        )}
        image nombre genres description rating releasedate platforms
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {};
}

function mapDispatchToProps(dispatch) {
  return {
    getVideogame: (id) => dispatch(searchVideogame(id)),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(DetailGame);
