import React, { Component } from "react";
import { connect } from "react-redux";
import { searchVideogame } from "../../actions";

export class DetailGame extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoaded: false,
    };
  }

  componentDidMount() {
    const { id } = this.props.match.params;
    this.props.getVideogame({ id }).then(() => {
      this.setState({ isLoaded: true });
      console.log("GAME LOADED");
    });
  }

  render() {
    return (
      <div>
        {this.state.isLoaded && (
          <>
            <h1>Name: {this.props.detail.name}</h1>
            <h3>Description: {this.props.detail.description}</h3>
            <p>Rating: {this.props.detail.rating}</p>
            <p>Release date: {this.props.detail.releaseDate}</p>
            <img src={this.props.detail.image} alt="ni idea" />
          </>
        )}
        image nombre genres description rating releasedate platforms
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
      detail: state.videogames
  };
}

function mapDispatchToProps(dispatch) {
  return {
    getVideogame: (id) => dispatch(searchVideogame(id)),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(DetailGame);
