import React, { Component } from "react";
import { connect } from "react-redux";
import { getGenres } from "../../actions";

export class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      description: "",
      releaseDate: "",
      rating: "",
      genres: [],
      platforms: [],
      petition: {
        loading: false,
        error: null,
      },
      isSubmitted: false,
    };
  }

  componentDidMount() {
    this.setState({
      petition: {
        loading: true,
        error: null,
      },
    });
    this.props.getGenres().then(() => {
      this.setState({
        petition: {
          loading: false,
          error: null,
        },
        isSubmitted: true,
      });
    });
  }

  handleChange(event) {
    this.setState({ title: event.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();
    console.log("SUBMIT");
    // this.setState({
    //   petition: {
    //     loading: true,
    //     error: null,
    //   },
    // });
    // this.props.getGenres().then(() => {
    //   this.setState({
    //     petition: {
    //       loading: false,
    //       error: null,
    //     },
    //     isSubmitted: true,
    //   });
    // });
  }

  handleKeyPress = (e) => {
    if (e.keyCode === 13) {
      this.handleSubmit();
    }
  };

  genresList() {
    if (!this.state.petition.loading) {
      return this.props.genres.response.map((element, i) => {
        return (
          <option key={i} value={element.name}>
            {element.name}
          </option>
        );
      });
    }
  }

  render() {
    const { name, description, releaseDate, rating } = this.state;
    return (
      <div>
        <form onSubmit={(e) => this.handleSubmit(e)}>
          <div>
            <label htmlFor="name">name: </label>
            <input
              type="text"
              id="name"
              autoComplete="off"
              value={name}
              onChange={(e) => this.handleChange(e)}
            />
          </div>
          <div>
            <label htmlFor="description">description: </label>
            <input
              type="text"
              id="description"
              autoComplete="off"
              value={description}
              onChange={(e) => this.handleChange(e)}
            />
          </div>
          <div>
            <label htmlFor="releaseDate">release date: </label>
            <input
              type="text"
              id="releaseDate"
              autoComplete="off"
              value={releaseDate}
              onChange={(e) => this.handleChange(e)}
            />
          </div>
          <div>
            <label htmlFor="rating">rating: </label>
            <input
              type="number"
              id="rating"
              autoComplete="off"
              value={rating}
              onChange={(e) => this.handleChange(e)}
            />
          </div>
          <div>
            <label htmlFor="genres">genres: </label>
            <select
              name="genres"
              id="genres"
              multiple={true}
              value={this.state.genres}
              onChange={(e) => {
                var newArray = this.state.genres;
                var tmp = true;
                for (let i in newArray) {
                  if (newArray[i].name === e.target.value) {
                    delete newArray[i];
                    this.setState({
                      genres: newArray,
                    });
                    tmp = false;
                  }
                }
                if (tmp === false) {
                  return;
                }
                this.setState({
                  genres: [...this.state.genres, e.target.value],
                });
              }}
            >
              {this.state.isSubmitted && this.genresList()}
            </select>
          </div>
          <button type="submit">SEARCH</button>
        </form>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    genres: state.genres,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    getGenres: () => dispatch(getGenres()),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Home);
